/*
Forfatter: Andreas Olsen aols0228 og TE3 team
Link til kilde: https://docs.tabulareditor.com/common/CSharpScripts/Beginner/script-create-table-groups.html
Beskrivelse: Makro, der klargør partitionerne efter de er blevet importeret fra databricks
Change Log: (udskift denne med dine noter)
---------------------------------------------------------------
Ver. | Dato DD-MM-YYYY | Forfatter | Beskrivelse
1.0    19-06-2024        aols0228    Frigivelse af makro
*/

// Loop through all tables:
foreach(var table in Model.Tables) {
  if (table is CalculationGroupTable) {
    table.TableGroup = "Beregningsgruppe";
  } else if (!table.UsedInRelationships.Any() && table.Measures.Any(m => m.IsVisible)) {
    // Tables containing visible measures, but no relationships to other tables
    // table.TableGroup = "Beregninger"; // jeg har taget den ud, da det virker dobbeltkonfekt at have Beregningstabel i en gruppe
  } else if (table.UsedInRelationships.All(r => r.FromTable == table) && table.UsedInRelationships.Any()) {
    // Tables exclusively on the "many" side of relationships:
    table.TableGroup = "Fact";
  } else if (!table.UsedInRelationships.Any() && table is CalculatedTable && !table.Measures.Any()) {
    // Tables without any relationships, that are Calculated Tables and do not have measures:
    table.TableGroup = "Parameter";
  } else if (table.UsedInRelationships.Any(r => r.ToTable == table)) {
    // Tables on the "one" side of relationships:
    table.TableGroup = "Dim";
  } else {
    // All other tables:
    table.TableGroup = "Eventuelt";
  }
}

/*
Forfatter: Andreas Nordgaard aols0228 og ChatGPT
Beskrivelse: Makro, der opretter tabelgrupper ud fra præfikser
Change Log:
---------------------------------------------------------------
Ver. | Dato DD-MM-YYYY | Forfatter  | Beskrivelse
1.0  | 20-09-2024      | aols0228   | Frigivelse af makro
1.1  | 10-10-2024      | aols0228   | Gjort at makro ikke fjerner alle _ understregninger
*/

foreach (var table in Model.Tables) 
{
    // Find the index of the first underscore
    int underscoreIndex = table.Name.IndexOf('_');

    // Proceed only if an underscore is found
    if (underscoreIndex > -1)
    {
        // Extract and capitalize the part before the first underscore (table group)
        string tableGroup = char.ToUpper(table.Name[0]) + table.Name.Substring(1, underscoreIndex - 1).ToLower();

        // Extract the part after the first underscore and capitalize the first letter
        // Does not remove any additional underscores that may exist in the table name
        string newTableName = table.Name.Substring(underscoreIndex + 1).Trim();
        if (newTableName.Length > 0)
            newTableName = char.ToUpper(newTableName[0]) + newTableName.Substring(1);

        // Update table name and set table group
        table.Name = newTableName;
        table.TableGroup = tableGroup;
    }
}

/*
Forfatter: Andreas Nordgaard aols0228, ChatGPT og ...
Link til kilde: 
Beskrivelse: Makro, der fjerner præfikser fra tabelnavn ud fra en prædefineret liste
Change Log: (udskift denne med dine noter)
---------------------------------------------------------------
Ver. | Dato DD-MM-YYYY | Forfatter | Beskrivelse
1.0    10-10-2024        aols0228    Frigivelse af makro
*/

List<string> prefixesToRemove = new List<string>
{
    "Dim_",
    "Fact_",
    "RLS_",
    "Parameter_",
    "Bridge_"
};

foreach (var table in Model.Tables)
{
    // Skip processing if the table name is null or empty
    if (string.IsNullOrEmpty(table.Name))
    {
        continue;
    }

    // Remove specified prefixes from the table name
    foreach (var prefix in prefixesToRemove)
    {
        if (table.Name.StartsWith(prefix, StringComparison.OrdinalIgnoreCase))
        {
            // Remove the prefix while maintaining the original case of the rest of the table name
            table.Name = table.Name.Substring(prefix.Length);

            // Capitalize the first letter of the table name
            table.Name = char.ToUpper(table.Name[0]) + table.Name.Substring(1);

            break;  // Exit the inner loop once a prefix is matched
        }
    }
}

// Now, Model.Tables contains tables with modified names (without specified prefixes, original case preserved, first letter capitalized)

/*
Forfatter: Andreas Nordgaard aols0228 og ChatGPT
Beskrivelse: Makro, der fjerner tabelgrupper og indsætter dem som præfikser
Change Log:
---------------------------------------------------------------
Ver. | Dato DD-MM-YYYY | Forfatter  | Beskrivelse
1.0  | 19-06-2024      | aols0228   | Frigivelse af makro
*/

// Opdater alle tabellerne
foreach (var table in Model.Tables) 
{
    // Check if the table has a non-empty TableGroup
    if (!string.IsNullOrEmpty(table.TableGroup))
    {
        // Determine the table group name, add an underscore, and convert to lower case
        string tableGroup = table.TableGroup.ToLower() + "_";

        // Check if tableGroup (with underscore) is already part of the table name using Ordinal comparison
        if (!table.Name.StartsWith(tableGroup, StringComparison.Ordinal))
        {
            // Combine the lower-cased table group with the table name, trim, replace spaces, and convert to lower case
            string newTableName = (tableGroup + table.Name.Trim().Replace(" ", "")).ToLower();

            // Set the table name to the new value
            table.Name = newTableName;

            // Remove the table group after setting the name
            table.TableGroup = null;
        }
    }
}

/*
Forfatter: Andreas Nordgaard aols0228, ChatGPT og ...
Link til kilde: 
Beskrivelse: Makro, der fjerner tabelgrupper og indsætter viewnavnet som tabelnavn, hvis M-partition
Change Log: (udskift denne med dine noter)
---------------------------------------------------------------
Ver. | Dato DD-MM-YYYY | Forfatter | Beskrivelse
1.0    10-10-2024        aols0228    Frigivelse af makro
*/

string targetSubstring = "Schema{[Name="; // Define the target substring as a variable
string quote = "\""; // Define the quote character as a variable

foreach (var table in Model.Tables)
{
    // Check if the table has partitions
    if (table.Partitions.Count > 0)
    {
        foreach (var partition in table.Partitions.OfType<MPartition>())
        {
            // Extract the expression and handle null
            string expression = partition.Expression ?? string.Empty;

            if (expression.Contains(targetSubstring))
            {
                // Extract the substring after the target and find the first quoted item
                int startIndex = expression.IndexOf(targetSubstring) + targetSubstring.Length;

                // Ensure startIndex is valid before extracting the schema name
                if (startIndex >= targetSubstring.Length && startIndex < expression.Length)
                {
                    string subExpression = expression.Substring(startIndex).Trim();
                    var match = System.Text.RegularExpressions.Regex.Match(subExpression, $"{quote}([^\"']*){quote}");

                    if (match.Success && match.Groups.Count > 1)
                    {
                        // Extract the schema name from the match
                        string schemaName = match.Groups[1].Value.Trim();

                        // Set the table name directly to the schema name
                        table.Name = schemaName;

                        // Remove the table group (if any) after setting the name
                        table.TableGroup = null;
                    }
                }
            }
        }
    }
}

