    // Set format string for measures
    foreach (var measure in table.Measures)
    {
        if (measure.DataType == DataType.Int64)
        {
            measure.FormatString = formatStringInt64;
        }
    }
}

/*
Forfatter: Andreas Olsen aols0228
GitHub link: https://github.com/PowerBI-tips/TabularEditor-Scripts/blob/main/Basic/Hide%20Columns%20Ending%20with%20'Key'%20in%20Selected%20Tables.csx
Beskrivelse: Makro, der klargør partitionerne efter de er blevet importeret fra databricks
Change Log: (udskift denne med dine noter)
---------------------------------------------------------------
Ver. | Dato DD-MM-YYYY | Forfatter | Beskrivelse
1.0    19-06-2024        aols0228    Frigivelse af makro
*/

// List to store suffixes
var suffixes = new List<string> { "Key", "Id", "Nøgle" };

// Loop through all tables
foreach(var t in Model.Tables)
{
    // Loop through each suffix in the list
    foreach(var suffix in suffixes)
    {
        // Loop through all columns ending with the current suffix on the current table
        foreach(var c in t.Columns.Where(col => col.Name.EndsWith(suffix, StringComparison.OrdinalIgnoreCase)))
        {
            c.IsHidden = true;
            c.SummarizeBy = AggregateFunction.None;
        }
    }
}

/*
Forfatter: Andreas Olsen aols0228, Matt Allington and ChatGPT 4o
Link til kilde: https://github.com/PowerBI-tips/TabularEditor-Scripts/blob/main/Basic/Hide%20columns%20on%20the%20many%20side%20of%20a%20relationship.csx
Beskrivelse: Makro, der skjuler primærnøgler
Change Log: (udskift denne med dine noter)
---------------------------------------------------------------
Ver. | Dato DD-MM-YYYY | Forfatter | Beskrivelse
1.0    19-06-2024        aols0228    Frigivelse af makro
*/

/*
 * Title: Hide columns on the many side of a relationship  
 *
 * Author: Matt Allington, https://exceleratorbi.com.au  
 *
 * it is dangerous to use columns on the many side of a relationship as it can 
 * produce unexpected results, so it is a best practice to hide these columns
 * to discourage their use in reports.
 */

// Hide all columns on many side of a join

// Hide all columns on either side of a join and set the aggregate function to None
foreach (var r in Model.Relationships)
{
    // Hide columns on the 'From' side of the relationship
    var fromColumn = r.FromColumn.Name;
    var fromTable = r.FromTable.Name;
    Model.Tables[fromTable].Columns[fromColumn].IsHidden = true;
    Model.Tables[fromTable].Columns[fromColumn].SummarizeBy = AggregateFunction.None;

    // Hide columns on the 'To' side of the relationship
    var toColumn = r.ToColumn.Name;
    var toTable = r.ToTable.Name;
    Model.Tables[toTable].Columns[toColumn].IsHidden = true;
    Model.Tables[toTable].Columns[toColumn].SummarizeBy = AggregateFunction.None;
}

