    // This script is meant to format all measures with a default formatstring
Change Log: (udskift denne med dine noter)
---------------------------------------------------------------
Ver. | Dato DD-MM-YYYY | Forfatter | Beskrivelse
1.0    19-06-2024        aols0228    Frigivelse af makro
*/


foreach (var ms in Selected.Measures) {

	if (ms.IsHidden) continue;

	if (!string.IsNullOrWhiteSpace(ms.FormatString)) continue;

	if (ms.DataType == DataType.Int64) ms.FormatString = "#,##0";

	if (ms.DataType == DataType.Double || ms.DataType == DataType.Decimal) {
		if (ms.Name.Contains("#")
			|| ms.Name.IndexOf("QTY", StringComparison.OrdinalIgnoreCase) >= 0) ms.FormatString = "#,##0";
		else ms.FormatString = "#,##0.00";
	}
}

/*
Forfatter: Kurt Buhler
Link til kilde: https://gist.github.com/data-goblin/a0d5d93ecf1e6927de08084c194dcaf6
Beskrivelse: Makro, der udtrækker forbindelser mellem målinger, så de kan blive indsat på mermaid.live
Change Log: (udskift denne med dine noter)
---------------------------------------------------------------
Ver. | Dato DD-MM-YYYY | Forfatter | Beskrivelse
1.0    19-06-2024        aols0228    Frigivelse af makro
*/

string dependencies = "flowchart LR\n%% Measure dependancy mermaid flowchart";

foreach(var _measures in Model.AllMeasures )         
    {

    var _upstream           = _measures.DependsOn;
    var _upstream_measures  = _upstream.Measures.OfType<Measure>().Select(c => c).Distinct();

    dependencies += string.Format("\r\n\n%% [{1}] Dependancies:\n\t{0}[\"{1}\"]",

        // 0 - Lineage tag of the measure in scope
        _measures.LineageTag, 

        // 1 - Name of the measure in scope
        _measures.Name);

    foreach( var measure_dependencies in _upstream_measures )
        { 
        dependencies += string.Format("\r\n\t{2}[\"{3}\"] --> {0}[\"{1}\"]",
            
            // 0 - Lineage tag of the measure in scope
            _measures.LineageTag, 
            
            // 1 - Name of the measure in scope
            _measures.Name,
            
            // 2 - Lineage tag of the dependant measure
            measure_dependencies.LineageTag,      
            
            // 3 - Name of the dependant measure
            measure_dependencies.Name);
        }
    }

Info("1. Systemet har automatisk kopieret relationer imellem dine beregninger\n\n2. Åbn en browser og gå ind på https://mermaid.live\n\n3. Derefter indsæt fra din udklipsholder\n\n4. Se dine afhængigheder imellem beregninger\n\n5. Du skal først klikke OK førend relationerne bliver kopieret til din udklipsholder");
System.Windows.Forms.Clipboard.SetText(dependencies);
