// Opdater alle M-partitionerne
foreach (var table in Selected.Tables) 
{
    foreach (var partition in table.Partitions.OfType<MPartition>())
    {
        // Determine the table group name and add an underscore if not empty
        string tableGroup = table.TableGroup;
        if (!string.IsNullOrEmpty(tableGroup))
        {
            tableGroup += "_";
        }

        // Trim whitespace from ends, remove all spaces, then convert to lower case
        string mPartitionsNavn = "\"" + (tableGroup + partition.Name).Trim().Replace(" ", "").ToLower() + "\"";

        partition.Expression =
            "let\n" +
            "    Source = Databricks.Catalogs(Server, Cluster),\n" + // null),\n" +
            "    Database = Source{[Name=Catalog,Kind=\"Database\"]}[Data],\n" +
            "    Schema = Database{[Name=Schema,Kind=\"Schema\"]}[Data],\n" +
            "    Data = Schema{[Name=" + mPartitionsNavn + ",Kind=\"View\"]}[Data]\n" +
            "in\n" +
            "    Data";
    }
}

