using System;
using System.Windows.Forms;

class Program
{
    static void Main()
    {
        string userInput = Prompt.ShowDialog("Enter Value", "Input Box");

        if (userInput != null)
        {
            MessageBox.Show($"You entered: {userInput}", "Result", MessageBoxButtons.OK, MessageBoxIcon.Information);
        }
        else
        {
            MessageBox.Show("Operation canceled.", "Result", MessageBoxButtons.OK, MessageBoxIcon.Information);
        }
    }
}

public class Prompt
{
    public static string ShowDialog(string text, string caption)
    {
        // Create the form
        Form prompt = new Form();
        prompt.Width = 500;
        prompt.Height = 220; // Set the height to ensure visibility
        prompt.Text = caption;
        prompt.FormBorderStyle = FormBorderStyle.FixedDialog;
        prompt.StartPosition = FormStartPosition.CenterScreen; // Center on screen initially
        prompt.MinimizeBox = false;
        prompt.MaximizeBox = false;
        prompt.TopMost = true; // Ensure the form stays on top

        // Bring the form to front and activate it
        prompt.Shown += (sender, e) =>
        {
            prompt.BringToFront();
            prompt.Activate();
        };

        // Create a label
        Label textLabel = new Label()
        {
            Left = 50,
            Top = 20,
            Width = 400,
            Text = text
        };

        // Create a text box
        TextBox textBox = new TextBox()
        {
            Left = 50,
            Top = 50,
            Width = 400,
            Height = 30 // Set height for better visibility
        };

        // Create the OK button
        Button confirmation = new Button()
        {
            Text = "OK",
            Left = 230,  // Positioned before "Annuller"
            Width = 100,
            Height = 40, // Ensure height is sufficient
            Top = 100 // Adjust position to avoid clipping
        };

        // Create the "Annuller" button
        Button cancelation = new Button()
        {
            Text = "Annuller",
            Left = 350, // Positioned after "OK"
            Width = 100,
            Height = 40, // Ensure height is sufficient
            Top = 100 // Adjust position to avoid clipping
        };

        // Button click events
        confirmation.Click += (sender, e) =>
        {
            prompt.DialogResult = DialogResult.OK;
            prompt.Close();
        };

        cancelation.Click += (sender, e) =>
        {
            prompt.DialogResult = DialogResult.Cancel;
            prompt.Close();
        };

        // Add controls to the form
        prompt.Controls.Add(textBox);
        prompt.Controls.Add(confirmation);
        prompt.Controls.Add(cancelation);
        prompt.Controls.Add(textLabel);

        // Set the default button
        prompt.AcceptButton = confirmation;

        // Show the dialog and return the result
        return prompt.ShowDialog() == DialogResult.OK ? textBox.Text : null;
    }
}

